func userInitials(name string) string {
	if name == "" {
		return "?"
	}
	words := strings.Fields(name)
	if len(words) == 0 {
		return string([]rune(name)[0])
	}
	initials := ""
	for i, word := range words {
		if i >= 2 || len(word) == 0 {
			break
		}
		initials += string([]rune(word)[0])
	}
	return strings.ToUpper(initials)
}

